(function(tiny){tiny.PluginManager.requireLangPack("youtube");tiny.PluginManager.add("youtube",function(editor,url){function showDialog(){editor.windowManager.open({title:tinymce.util.I18n.translate("YouTube Title"),file:url+"/youtube.html",width:800,height:550,inline:1,resizable:true,maximizable:true})}editor.addButton("youtube",{icon:true,image:url+"/img/youtube.gif",tooltip:tinymce.util.I18n.translate("YouTube Tooltip"),onclick:showDialog,onPostRender:function(){var self=this;editor.on("NodeChange",function(e){self.active(e.element.nodeName==="IMG")})}})})})(tinymce);